function() {
  // gtm.start = timestamp when GTM loaded (same for entire page session)
  // gtm.uniqueEventId = sequential number per dataLayer event (same for ALL tags on same trigger)
  // Combined = deterministic, unique per event, identical across all tags
  var ts = window.google_tag_manager && window.google_tag_manager["GTM-TNM9JZ3S"] ? 
           window.google_tag_manager["GTM-TNM9JZ3S"].dataLayer.get("gtm.start") : Date.now();
  var uid = window.google_tag_manager && window.google_tag_manager["GTM-TNM9JZ3S"] ? 
            window.google_tag_manager["GTM-TNM9JZ3S"].dataLayer.get("gtm.uniqueEventId") : "0";
  return String(ts) + "_" + String(uid);
}

function() {
  // Page load timestamp - constant for the entire page session
  if (!window._fbPageTs) {
    window._fbPageTs = String(Date.now());
  }
  // gtm.uniqueEventId - same for ALL tags firing on the same event
  var gtmUniqueId = google_tag_manager["GTM-TNM9JZ3S"].dataLayer.get("gtm.uniqueEventId") || "0";
  // Combine: deterministic, unique per event, same across all tags
  return window._fbPageTs + "_" + String(gtmUniqueId);
}

